mapper.select:
SELECT ID AS id,WEBSITE AS website,NEWS_TYPE AS newsType,URL AS url,STATUS AS statusCode,
(CASE STATUS WHEN 0 THEN '禁用' ELSE '启用' END) AS status,DATE_FORMAT(TIME,'%Y-%m-%d %H:%i:%s') AS time
FROM website_config
WHERE 1=1


  AND WEBSITE LIKE #{pattern}


  AND DATE_FORMAT(TIME,'%Y-%m-%d')>=#{startTime}


  AND DATE_FORMAT(TIME,'%Y-%m-%d')<=#{endTime}

mapper.insert:
insert into website_config (ID, WEBSITE, NEWS_TYPE, 
  URL, STATUS, TIME
  )
values (#{id,jdbcType=BIGINT}, #{website,jdbcType=VARCHAR}, #{newsType,jdbcType=VARCHAR}, 
  #{url,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{time,jdbcType=TIMESTAMP}
  )
